    top: 0%;
    opacity: 0;
  }
  5% {
    opacity: 1;
  }
  95% {
    opacity: 1;
  }
  100% {
    top: 100%;
    opacity: 0;
  }
}

.scan-line {
  animation: scanLine 10s linear infinite;
}

animations.framer_motion_examples:
// Scroll-triggered section animation
const sectionVariants = {
  hidden: { 
    opacity: 0, 
    y: 40 
  },
  visible: { 
    opacity: 1, 
    y: 0,
    transition: {
      duration: 0.5,
      ease: [0.25, 0.46, 0.45, 0.94],
      staggerChildren: 0.1
    }
  }
};

// Use with useInView hook
<motion.section
  variants={sectionVariants}
  initial="hidden"
  whileInView="visible"
  viewport={{ once: true, amount: 0.3 }}
>
  {children}
</motion.section>

// Risk gauge arc animation
const gaugeVariants = {
  hidden: { pathLength: 0 },
  visible: { 
    pathLength: riskScore,
    transition: {
      duration: 1.2,
      ease: [0.34, 1.56, 0.64, 1] // spring overshoot
    }
  }
};

// Staggered card entrance
const containerVariants = {
  visible: {
    transition: {
      staggerChildren: 0.15
    }
  }
};

const cardVariants = {
  hidden: { opacity: 0, y: 40, scale: 0.98 },
  visible: { 
    opacity: 1, 
    y: 0, 
    scale: 1,
    transition: { duration: 0.5 }
  }
};

