@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }

  .scan-line,
  .parallax {
    display: none;
  }
}

gaze_data_format.structure:
{
  "sessionId": "demo-session-001",
  "duration": 2700, // seconds (45 minutes)
  "metadata": {
    "type": "normal" | "suspicious",
    "offScreenEvents": number,
    "attentionScore": number,
    "readingPatternDetected": boolean
  },
  "points": [
    {
      "timestamp": 0, // seconds from start
      "x": 0.52, // normalized 0-1 (relative to screen)
      "y": 0.48, // normalized 0-1
      "duration": 150, // milliseconds at this position
      "isOffScreen": false,
      "direction": null | "left" | "right" | "top" | "bottom"
    },
    // ... hundreds of points
  ]
}

security_events_format.structure:
{
  "sessionId": "demo-session-001",
  "events": [
    {
      "id": "evt-001",
      "timestamp": 45, // seconds from start
      "type": "window_focus" | "clipboard" | "process" | "screen_recording" | "vm_detected",
      "severity": "info" | "warning" | "critical",
      "title": "Window Focus Change",
      "description": "Returned to interview window",
      "metadata": {
        // Type-specific metadata
      }
    }
  ]
}

ai_analysis_format.structure:
