  "questionId": "q-001",
  "question": "Explain the difference between TCP and UDP protocols.",
  "answer": "...", // The candidate's answer
  "analysis": {
    "similarity": {
      "gpt4": 0.23,
      "claude": 0.19,
      "gemini": 0.21
    },
    "perplexity": 42.3,
    "ngramOverlap": 0.15,
    "stylometricScore": 0.88, // How "human" the writing style is
    "riskScore": 0.21,
    "riskLevel": "minimal", // minimal | low | medium | high
    "flags": []
  }
}
